export const orderColumns = [
  { id: 1, field: "customerName", headerName: "Customer Name", width: 200 },
  {
    id: 2,
    field: "customerPO",
    headerName: "Customer PO",
    width: 161,
    renderCell: (params) => {
      return (
        <div className="not-hover">
          {params.value}
        </div>
      );
    },
  },
  {
    id: 3,
    field: 'customerPODate',
    headerName: 'PO Date',
    width: 188,
    renderCell: (params) => {
      if (params.value === null || params.value === undefined) {
        return '';
      }
      const date = new Date(params.value);
      const options = { year: 'numeric', month: 'short', day: '2-digit' };
      return date.toLocaleDateString('en-US', options);
    },
  },

  {
    id: 4, field: "TotalQtySo", headerName: "PO Value", width: 200, align: 'right', headerAlign: 'right', sortComparator: (v1, v2) => {
        // Remove currency symbols and commas and parse the values as floats
        const value1 = parseFloat(v1.replace(/[$,]/g, ''));
        const value2 = parseFloat(v2.replace(/[$,]/g, ''));
      
        // Compare the parsed values
        if (value1 < value2) {
          return -1;
        }
        if (value1 > value2) {
          return 1;
        }
        return 0;
    }
  },
  { id: 5, field: "orderStatus", headerName: "Status", width: 150, }
];

export const poDetailColumns = [
  {
    id: 1, field: "CustomerPartNumber",
    headerName: "Customer Part #",
    width: 153,
  },
  {
    id: 2, field: "CustomerPNDescription",
    headerName: "P.N. Description",
    width: 214,
  },
  {
    id: 3, field: "QtySo", headerName: "QTY Ordered", width: 129, align: 'right', headerAlign: 'right', valueFormatter: (params) => {
      const formattedQuantity = Number(params.value).toLocaleString();
      return formattedQuantity;
    },
  },
  { id: 4, field: "Units", headerName: "Units", width: 50 },
  {
    id: 5,
    field: "AmountDelivered",
    headerName: "QTY Delivered",
    width: 170,
    renderCell: (params) => {
      const formattedValue = Number(params.value).toLocaleString();
      return (
        <div className="not-hover">
          {formattedValue}
        </div>
      );
    },
    align: 'right',
    headerAlign: 'right',
  },
  {
    id: 6, field: "BalanceToDeliver", headerName: "Balance to Deliver", width: 165, align: 'right', headerAlign: 'right', valueFormatter: (params) => {
      const formattedQuantity = Number(params.value).toLocaleString();
      return formattedQuantity;
    },
  },
  {
    id: 7, field: "UpdatedDeliveryDate",
    headerName: "Delivery Date",
    width: 196,
    renderCell: (params) => {
      if (params.value === null || params.value === undefined) {
        return '';
      }
      const date = new Date(params.value);
      const options = { year: 'numeric', month: 'short', day: '2-digit' };
      return date.toLocaleDateString('en-US', options);
    },
  },
  { id: 8, field: "ShippingTerm", headerName: "Shipping Term", width: 150 },
  { id: 9, field: "SONumber", headerName: "SO Nmuber", width: 143 },
];

export const deliveryColumns = [
  { id: 1, field: "DeliveryDate", headerName: "Delivery Date", width: 200 },
  { id: 2, field: "AmountDelivered", headerName: "QTY Deliverd", width: 200 },
  { id: 3, field: "AccumulatedAmount", headerName: "Accumulated QTY", width: 200 },
];

export const deliveryOrderColumns = [
  { id: 1, field: "CustomerPO", headerName: "Customer PO", width: 143 },
  {
    id: 2, field: "CustomerPODate",
    headerName: "PO Date",
    width: 129,
    renderCell: (params) => {
      if (params.value === null || params.value === undefined) {
        return '';
      }
      const date = new Date(params.value);
      const options = { year: 'numeric', month: 'short', day: '2-digit' };
      return date.toLocaleDateString('en-US', options);
    },
  },
  {
    id: 3, field: "CustomerPNDescription",
    headerName: "P.N. Description",
    width: 214,
  },
  {
    id: 4, field: "QtySo", headerName: "QTY Ordered", width: 129, align: 'right', valueFormatter: (params) => {
      const formattedQuantity = Number(params.value).toLocaleString();
      return formattedQuantity;
    },
  },
  { id: 5, field: "Units", headerName: "Units", width: 50 },
  {
    id: 6,
    field: "AmountDelivered",
    headerName: "QTY Delivered",
    width: 170,
    renderCell: (params) => {
      const formattedValue = Number(params.value).toLocaleString();
      return (
        <div className="not-hover">
          {formattedValue}
        </div>
      );
    },
    align: 'right',
  },
]
